import { Component } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { FaceSnap } from '../models/face-snap.model';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-new-face-snap',
  templateUrl: './new-face-snap.component.html',
  styleUrls: ['./new-face-snap.component.scss']
})
export class NewFaceSnapComponent {

  snapForm!: FormGroup;
  faceSnapPreview$!: Observable<FaceSnap>;

  constructor (private formBuilder: FormBuilder) {}

  ngOnInit(): void {
    this.snapForm = this.formBuilder.group({
      title: [null],
      description: [null],
      imageUrl: [null],
      location: [null]
    })
  }

  onSubmitForm(): void {
    console.log(this.snapForm.value);
  }

}
<div class="form-card">
  <h2>NOUVEAU FACESNAP</h2>
  <form [formGroup]="snapForm">
    <div class="form-group">
      <label for="title">Titre</label>
      <input id="title" type="text" formControlName="title" />
    </div>
    <div class="form-group">
      <label for="description">Description</label>
      <textarea
        id="description"
        type="text"
        formControlName="description"
        rows="4"
      >
      </textarea>
    </div>
    <div class="form-group">
      <label for="imageUrl">URL de l'image</label>
      <input id="imageUrl" type="text" formControlName="imageUrl" />
    </div>
    <div class="form-group">
      <label for="location">Lieu</label>
      <input id="location" type="text" formControlName="location" />
    </div>
    <div class="action-buttons">
      <button type="submit" (click)="onSubmitForm()">Enregistrer</button>
    </div>
  </form>
</div>
